scriptTask.script:
String _userid = (String)kcontext.getVariable("approvers");
String _plannerid = (String)kcontext.getVariable("userIds");
String _approvers = _userid;
if(_approvers == null || "".equals(_approvers.trim()) ){
	_approvers=_plannerid;
}else{
	if(_plannerid != null && !"".equals(_plannerid.trim())){
		_approvers = _approvers + "," + _plannerid;
	}
}
if(_approvers ==null || "".equals(_approvers.trim())){
	kcontext.setVariable("approvers","-99999");
}else{
	kcontext.setVariable("approvers",_approvers);
}
